        db.execSQL("insert into \n"+ TABLE_CONT + " values('8','№ 8. Установление предварительного позитивного фрейма намерения')");
        db.execSQL("insert into \n"+ TABLE_CONT + " values('9','№ 9. Установление предварительного позитивного фрейма причины')");
